export class ShoppingCart{static cartList=[];static restoreShoppingCart(){ShoppingCart.cartList=JSON.parse(localStorage.getItem("carrito"))??[]}static saveShoppingCart(){let t=JSON.stringify(ShoppingCart.cartList);return localStorage.setItem("carrito",t),"Ok"}static calculateTotalAmount(){return ShoppingCart.cartList.reduce(((t,r)=>t+r.precio),0).toLocaleString("es-AR",{style:"currency",currency:"ARS"})}static removeItem(t){if("number"==typeof parseInt(t)){let r=ShoppingCart.cartList.findIndex((r=>r.id===t));if(r>-1){return ShoppingCart.cartList.splice(r,1)}return"⛔️ Error al quitar el producto."}return"⛔️ Error en el dato enviado."}static calculateTotalProducts(){return ShoppingCart.cartList.length>0?ShoppingCart.cartList.length:0}}